inherited.ScriptText.Text:
var
  CalcHeightReportSummary: Extended;
  CalcHeightTextUeberEndsumme   : Extended;                                                                                           
  CalcHeightTextUnterEndsumme   : Extended;
  CalcHeightAuslandstext        : Extended;
  CalcHeightBankverbindungstext : Extended;
  CalcHeightZBtext              : Extended;
  CalcHeightEntgelttext         : Extended;                                                                                                                                
  Oem_Info_TopPos               : Extended;                                                                   
        


//---------------------------------------------------------------------------------------------------------------------
// HÃ¶he der einzelnen Child's berechnen                                                                                                          
//---------------------------------------------------------------------------------------------------------------------
procedure ReportSummary1OnAfterCalcHeight(Sender: TfrxComponent);
var
  CurY: Extended;                                      
begin
  if not Columnfooter1.Visible then
  begin
    ColumnFooter1.Height := 0;
  end;
  CalcHeightReportSummary := ReportSummary1.height;                                                  
end;

procedure Child_TextUeberEndsummeOnAfterCalcHeight(Sender: TfrxComponent);
begin
  CalcHeightTextUeberEndsumme := Child_TextUeberEndsumme.Height;
end;
  
procedure Child_TextUnterEndsummeOnAfterCalcHeight(Sender: TfrxComponent);
begin
  CalcHeightTextUnterEndsumme := Child_TextUnterEndsumme.Height;                                          
end;

procedure Child_ZahlungsbedingungOnAfterCalcHeight(Sender: TfrxComponent);
begin
  CalcHeightZBtext := Child_Zahlungsbedingung.Height;                                        
end;

procedure Child_BankverbindungstextOnAfterCalcHeight(Sender: TfrxComponent);
begin
  CalcHeightBankverbindungstext := Child_Bankverbindungstext.Height;                                        
end;

procedure Child_AuslandstextOnAfterCalcHeight(Sender: TfrxComponent);
begin
  CalcHeightAuslandstext := Child_Auslandstext.Height;                                                              
end;

procedure Child_EntgelttextOnAfterCalcHeight(Sender: TfrxComponent);
begin
  CalcHeightEntgelttext := Child_Entgelttext.Height;                                                                     
end;
    

//---------------------------------------------------------------------------------------------------------------------
//---------------------------------------------------------------------------------------------------------------------


//---------------------------------------------------------------------------------------------------------------------
// Child's OnBeforePrint                                        
//---------------------------------------------------------------------------------------------------------------------
    

procedure ReportSummary1OnBeforePrint(Sender: TfrxComponent);
var
  CurY: Extended;
begin
  ReportSummary1.Child            := Child_TextUnterEndsumme;
  Child_TextUnterEndsumme.Child   := Child_Zahlungsbedingung;
  Child_Zahlungsbedingung.Child   := Child_Bankverbindungstext;                                       
  Child_Bankverbindungstext.Child := Child_Auslandstext;
  Child_Auslandstext.Child        := Child_Entgelttext;
  if Engine.FinalPass then
  begin
    CurY := GetReportSummaryHeight;                                        
    CurY := CurY - 1;                                                  
    if Engine.CurY - 1 <= CurY then                                                                
      Engine.CurY := CurY
    else
    begin                
      Engine.NewPage;                                                     
      Engine.CurY := CurY;
    end;                
  end;                    
end;

procedure PageHeader1OnBeforePrint(Sender: TfrxComponent);
begin
  Child_TextUnterEndsumme.Visible := HasText_TextUnterEndsumme;
  Child_Zahlungsbedingung.Visible := HasText_ZB;
  // rv_SchriftKalbach.RichEdit.Font.Color := $592C00;
  // rv_SchriftNieder.RichEdit.Font.Color := $592C00;
end;

procedure Child_TextUeberEndsummeOnBeforePrint(Sender: TfrxComponent);
var
  CurY: Extended;
begin
  if Engine.FinalPass then
  begin
    CurY := GetReportSummaryHeight - CalcHeightTextUeberEndsumme;                                        
    if Engine.CurY <= CurY then                                                                
      Engine.CurY := CurY
    else
    begin                
      Engine.NewPage;
      Engine.CurY := CurY;  
    end;                
  end;                    
end;


function GetReportSummaryHeight: Extended;
var
  RealHeight: Integer;                                                                 
begin
  RealHeight := 0;
  if CalcHeightTextUnterEndsumme > 0 then    RealHeight := RealHeight + 5;
  if CalcHeightAuslandstext > 0 then         RealHeight := RealHeight + 5;
  if CalcHeightBankverbindungstext > 0 then  RealHeight := RealHeight + 5;
  if CalcHeightZBtext > 0 then               RealHeight := RealHeight + 5;
  if CalcHeightEntgelttext > 0 then          RealHeight := RealHeight + 5;
       
  if RealHeight > 0 then
    RealHeight := RealHeight -5;

  Result := Engine.PageHeight -CalcHeightReportSummary - Pagefooter1.Height;
  Result := Result - CalcHeightTextUnterEndsumme - CalcHeightAuslandstext - CalcHeightBankverbindungstext - CalcHeightZBtext;
  Result := Result - CalcHeightEntgelttext;
  Result := Result + RealHeight;
      
end;          
    
  

procedure rv_Pos_Oem_InfoOnBeforePrint(Sender: TfrxComponent);
var
  s: string;                              
begin
  rv_Pos_Bezeichnung.Top  := Oem_Info_TopPos;                                 
  rv_Pos_OEM_Info.Visible := true;                                                     
  s := '';                           
  if Trim(<qry_pos."AR_OEM">) > '' then
    s := s + 'OEM: ' + <qry_pos."AR_OEM"> + '  ';
  if Trim(<qry_pos."AR_KAPAZITAET">) > '' then
    s := s + 'Info: ' + <qry_pos."AR_KAPAZITAET">;
  rv_Pos_OEM_Info.RichEdit.Lines.Text := s;
  if s = '' then
  begin
    rv_Pos_OEM_Info.Visible := false;
    rv_Pos_Bezeichnung.Top  := rv_Pos_OEM_Info.Top;                                            
  end;            
end;



procedure rv_LieferterminOnBeforePrint(Sender: TfrxComponent);
begin
  rv_Liefertermin.RichEdit.Lines.Text := '';                    
  if <qry_Vorgang."VO_LIEFERSELECT"> = 1 then                                       
    rv_Liefertermin.RichEdit.Lines.Text := <qry_Vorgang."VO_LIEFERTERMIN">;                                                                 
  if <qry_Vorgang."VO_LIEFERSELECT"> = 2 then                                       
    rv_Liefertermin.RichEdit.Lines.Text := 'KW ' + IntToStr(<qry_Vorgang."VO_LIEFERKW">) + ' / ' + IntToStr(<qry_Vorgang."VO_LIEFERKWJ">);                                                                 
end;


function HasText_TextUnterEndsumme: Boolean;                     
begin
  Result := Trim(rv_TextUnterEndsumme.RichEdit.Lines.Text) > '';
  if (not Result) and (<TEXTUNTERENDSUMME> <> <NULL>) then
    Result := <TEXTUNTERENDSUMME> > ''
end;
  
function HasText_ZB: Boolean;
begin            
  Result := Trim(rv_Zahlungsbedingung.RichEdit.Lines.Text) > '';
  if (not Result) and (<ZB> <> <NULL>) then
    Result := Trim(<ZB>) > '';
end;

function HasText_TextUeberEndsumme: Boolean;                     
begin
  Result := Trim(rv_TextUeberEndsumme.RichEdit.Lines.Text) > '';
  if (not Result) and (<TEXTUEBERENDSUMME> <> <NULL>) then
    Result := <TEXTUEBERENDSUMME> > ''
end;    

procedure PageFooter1OnBeforePrint(Sender: TfrxComponent);
begin
  rv_Url.RichEdit.Font.Color := clWhite;  
end;

begin
  CalcHeightReportSummary       := 0;
  CalcHeightTextUeberEndsumme   := 0;
  CalcHeightTextUnterEndsumme   := 0;
  CalcHeightAuslandstext        := 0;
  CalcHeightBankverbindungstext := 0;
  CalcHeightZBtext              := 0;
  CalcHeightEntgelttext         := 0;
  Oem_Info_TopPos := rv_Pos_Bezeichnung.Top;                       
end.
